import { AcquisitionManager as Sdk } from "code-push/script/acquisition-sdk";
import { NativeEventEmitter } from "react-native";
import RestartManager from "./RestartManager";

// This function is used to augment remote and local
// package objects with additional functionality/properties
// beyond what is included in the metadata sent by the server.
module.exports = (NativeCodePush) => {
  const remote = (reportStatusDownload) => {
    return {
      async download(downloadProgressCallback) {
        if (!this.downloadUrl) {
          throw new Error("Cannot download an update without a download url");
        }

        let downloadProgressSubscription;
        if (downloadProgressCallback) {
          const codePushEventEmitter = new NativeEventEmitter(NativeCodePush);
          // Use event subscription to obtain download progress.
          downloadProgressSubscription = codePushEventEmitter.addListener(
            "CodePushDownloadProgress",
            downloadProgressCallback
          );
        }

        // Use the downloaded package info. Native code will save the package info
        // so that the client knows what the current package version is.
        try {
          const downloadedPackage = await NativeCodePush.downloadUpdate(this, !!downloadProgressCallback);
          reportStatusDownload && reportStatusDownload(this);
          return { ...downloadedPackage, ...local };
        } finally {
          downloadProgressSubscription && downloadProgressSubscription.remove();
        }
      },

      isPending: false // A remote package could never be in a pending state
    };
  };

  const local = {
    async install(installMode = NativeCodePush.codePushInstallModeOnNextRestart, minimumBackgroundDuration = 0, updateInstalledCallback) {
      const localPackage = this;
      const localPackageCopy = Object.assign({}, localPackage); // In dev mode, React Native deep freezes any object queued over the bridge
      await NativeCodePush.installUpdate(localPackageCopy, installMode, minimumBackgroundDuration);
      updateInstalledCallback && updateInstalledCallback();
      if (installMode == NativeCodePush.codePushInstallModeImmediate) {
        RestartManager.restartApp(false);
      } else {
        RestartManager.clearPendingRestart();
        localPackage.isPending = true; // Mark the package as pending since it hasn't been applied yet
      }
    },

    isPending: false // A local package wouldn't be pending until it was installed
  };

  return { local, remote };
};

const packageJson = require("./package.json");

module.exports = {
  async request(verb, url, requestBody, callback) {
    if (typeof requestBody === "function") {
      callback = requestBody;
      requestBody = null;
    }

    const headers = {
      "Accept": "application/json",
      "Content-Type": "application/json",
      "X-CodePush-Plugin-Name": packageJson.name,
      "X-CodePush-Plugin-Version": packageJson.version,
      "X-CodePush-SDK-Version": packageJson.dependencies["code-push"]
    };

    if (requestBody && typeof requestBody === "object") {
      requestBody = JSON.stringify(requestBody);
    }

    try {
      const response = await fetch(url, {
        method: getHttpMethodName(verb),
        headers: headers,
        body: requestBody
      });

      const statusCode = response.status;
      const body = await response.text();
      callback(null, { statusCode, body });
    } catch (err) {
      callback(err);
    }
  }
};

function getHttpMethodName(verb) {
  // Note: This should stay in sync with the enum definition in
  // https://github.com/Microsoft/code-push/blob/master/sdk/script/acquisition-sdk.ts#L6
  return [
    "GET",
    "HEAD",
    "POST",
    "PUT",
    "DELETE",
    "TRACE",
    "OPTIONS",
    "CONNECT",
    "PATCH"
  ][verb];
}

const log = require("./logging");
const NativeCodePush = require("react-native").NativeModules.CodePush;

const RestartManager = (() => {
    let _allowed = true;
    let _restartInProgress = false;
    let _restartQueue = [];

    function allow() {
        log("Re-allowing restarts");
        _allowed = true;

        if (_restartQueue.length) {
            log("Executing pending restart");
            restartApp(_restartQueue.shift(1));
        }
    }

    function clearPendingRestart() {
        _restartQueue = [];
    }

    function disallow() {
        log("Disallowing restarts");
        _allowed = false;
    }

    async function restartApp(onlyIfUpdateIsPending = false) {
        if (_restartInProgress) {
            log("Restart request queued until the current restart is completed");
            _restartQueue.push(onlyIfUpdateIsPending);
        } else if (!_allowed) {
            log("Restart request queued until restarts are re-allowed");
            _restartQueue.push(onlyIfUpdateIsPending);
        } else {
            _restartInProgress = true;
            if (await NativeCodePush.restartApp(onlyIfUpdateIsPending)) {
                // The app has already restarted, so there is no need to
                // process the remaining queued restarts.
                log("Restarting app");
                return;
            }

            _restartInProgress = false;
            if (_restartQueue.length) {
                restartApp(_restartQueue.shift(1));
            }
        }
    }

    return {
        allow,
        clearPendingRestart,
        disallow,
        restartApp
    };
})();

module.exports = RestartManager;

/*
 * This script generates a hash of all the React Native bundled assets and writes it into
 * into the APK. The hash in "updateCheck" requests to prevent downloading an identical
 * update to the one already present in the binary.
 *
 * It first creates a snapshot of the contents in the resource directory by creating
 * a map with the modified time of all the files in the directory. It then compares this
 * snapshot with the one saved earlier in "recordFilesBeforeBundleCommand.js" to figure
 * out which files were generated by the "react-native bundle" command. It then computes
 * the hash for each file to generate a manifest, and then computes a hash over the entire
 * manifest to generate the final hash, which is saved to the APK's assets directory.
 */

var crypto = require("crypto");
var fs = require("fs");
var path = require("path");

var getFilesInFolder = require("./getFilesInFolder");

var CODE_PUSH_FOLDER_PREFIX = "CodePush";
var CODE_PUSH_HASH_FILE_NAME = "CodePushHash";
var CODE_PUSH_HASH_OLD_FILE_NAME = "CodePushHash.json";
var HASH_ALGORITHM = "sha256";
var TEMP_FILE_PATH = path.join(require("os").tmpdir(), "CodePushResourcesMap.json");

var resourcesDir = process.argv[2];
var jsBundleFilePath = process.argv[3];
var assetsDir = process.argv[4];
var resourceFiles = [];

getFilesInFolder(resourcesDir, resourceFiles);

var oldFileToModifiedTimeMap = require(TEMP_FILE_PATH);
var newFileToModifiedTimeMap = {};

resourceFiles.forEach(function(resourceFile) {
    newFileToModifiedTimeMap[resourceFile.path.substring(resourcesDir.length)] = resourceFile.mtime;
});

var bundleGeneratedAssetFiles = [];

for (var newFilePath in newFileToModifiedTimeMap) {
    if (!oldFileToModifiedTimeMap[newFilePath] || oldFileToModifiedTimeMap[newFilePath] < newFileToModifiedTimeMap[newFilePath].getTime()) {
        bundleGeneratedAssetFiles.push(newFilePath);
    }
}

var manifest = [];

if (bundleGeneratedAssetFiles.length) {
    bundleGeneratedAssetFiles.forEach(function(assetFile) {
        // Generate hash for each asset file
        addFileToManifest(resourcesDir, assetFile, manifest, function() {
            if (manifest.length === bundleGeneratedAssetFiles.length) {
                addJsBundleAndMetaToManifest();
            }
        });
    });
} else {
    addJsBundleAndMetaToManifest();
}

function addJsBundleAndMetaToManifest() {
    addFileToManifest(path.dirname(jsBundleFilePath), path.basename(jsBundleFilePath), manifest, function() {
        var jsBundleMetaFilePath = jsBundleFilePath + ".meta";
        addFileToManifest(path.dirname(jsBundleMetaFilePath), path.basename(jsBundleMetaFilePath), manifest, function() {
            manifest = manifest.sort();
            var finalHash = crypto.createHash(HASH_ALGORITHM)
                .update(JSON.stringify(manifest))
                .digest("hex");

            console.log(finalHash);

            var savedResourcesManifestPath = assetsDir + "/" + CODE_PUSH_HASH_FILE_NAME;
            fs.writeFileSync(savedResourcesManifestPath, finalHash);

            // "CodePushHash.json" file name breaks flow type checking.
            // To fix the issue we need to delete "CodePushHash.json" file and
            // use "CodePushHash" file name instead to store the hash value.
            // Relates to https://github.com/Microsoft/react-native-code-push/issues/577
            var oldSavedResourcesManifestPath = assetsDir + "/" + CODE_PUSH_HASH_OLD_FILE_NAME;
            if (fs.existsSync(oldSavedResourcesManifestPath)) {
                fs.unlinkSync(oldSavedResourcesManifestPath);
            }
        });
    });
}

function addFileToManifest(folder, assetFile, manifest, done) {
    var fullFilePath = path.join(folder, assetFile);
    if (!fileExists(fullFilePath)) {
        done();
        return;
    }

    var readStream = fs.createReadStream(path.join(folder, assetFile));
    var hashStream = crypto.createHash(HASH_ALGORITHM);

    readStream.pipe(hashStream)
        .on("error", function(error) {
            throw error;
        })
        .on("finish", function() {
            hashStream.end();
            var buffer = hashStream.read();
            var fileHash = buffer.toString("hex");
            manifest.push(path.join(CODE_PUSH_FOLDER_PREFIX, assetFile).replace(/\\/g, "/") + ":" + fileHash);
            done();
        });
}

function fileExists(file) {
    try { return fs.statSync(file).isFile(); }
    catch (e) { return false; }
}

fs.unlinkSync(TEMP_FILE_PATH);

var fs = require("fs");
var path = require("path");

// Utility function that collects the stats of every file in a directory
// as well as in its subdirectories.
function getFilesInFolder(folderName, fileList) {
    var folderFiles = fs.readdirSync(folderName);
    folderFiles.forEach(function(file) {
        var fileStats = fs.statSync(path.join(folderName, file));
        if (fileStats.isDirectory()) {
            getFilesInFolder(path.join(folderName, file), fileList);
        } else {
            fileStats.path = path.join(folderName, file);
            fileList.push(fileStats);
        }
    });
}

module.exports = getFilesInFolder;

/*
 * This script creates a snapshot of the contents in the resource directory
 * by creating a map with the modified time of all the files in the directory
 * and saving it to a temp file. This snapshot is later referenced in 
 * "generatePackageHash.js" to figure out which files have changed or were
 * newly generated by the "react-native bundle" command.
 */

var fs = require("fs");
var path = require("path");

var getFilesInFolder = require("./getFilesInFolder");

var TEMP_FILE_PATH = path.join(require("os").tmpdir(), "CodePushResourcesMap.json");

var resourcesDir = process.argv[2];
var resourceFiles = [];

try {
    getFilesInFolder(resourcesDir, resourceFiles);
} catch(error) {
    var targetPathNotFoundExceptionMessage = "\nResources directory path does not exist.\n";
    targetPathNotFoundExceptionMessage += "Unable to find '" + resourcesDir;
    targetPathNotFoundExceptionMessage += "' directory. Please check version of Android Plugin for Gradle.";
    error.message += targetPathNotFoundExceptionMessage;
    throw error;
}

var fileToModifiedTimeMap = {};

resourceFiles.forEach(function(resourceFile) {
    fileToModifiedTimeMap[resourceFile.path.substring(resourcesDir.length)] = resourceFile.mtime.getTime();
});

fs.writeFile(TEMP_FILE_PATH, JSON.stringify(fileToModifiedTimeMap), function(err) {
    if (err) {
        throw err;
    }
});

var fs = require("fs");
var glob = require("glob");
var inquirer = require('inquirer');
var path = require("path");
var plist = require("plist");
var xcode = require("xcode");

var package = require('../../../../../package.json');

var ignoreNodeModules = { ignore: "node_modules/**" };
var appDelegatePaths = glob.sync("**/AppDelegate.+(mm|m)", ignoreNodeModules);

// Fix for https://github.com/Microsoft/react-native-code-push/issues/477
// Typical location of AppDelegate.m for newer RN versions: $PROJECT_ROOT/ios/<project_name>/AppDelegate.m
// Let's try to find that path by filtering the whole array for any path containing <project_name>
// If we can't find it there, play dumb and pray it is the first path we find.
var appDelegatePath = findFileByAppName(appDelegatePaths, package ? package.name : null) || appDelegatePaths[0];

if (!appDelegatePath) {
    console.log(`Couldn't find AppDelegate. You might need to update it manually \
Please refer to plugin configuration section for iOS at \
https://github.com/microsoft/react-native-code-push#plugin-configuration-ios`);
    return;
}

var appDelegateContents = fs.readFileSync(appDelegatePath, "utf8");

// 1. Add the header import statement
var codePushHeaderImportStatement = `#import <CodePush/CodePush.h>`;
if (~appDelegateContents.indexOf(codePushHeaderImportStatement)) {
    console.log(`"CodePush.h" header already imported.`);
} else {
    var appDelegateHeaderImportStatement = `#import "AppDelegate.h"`;
    appDelegateContents = appDelegateContents.replace(appDelegateHeaderImportStatement,
        `${appDelegateHeaderImportStatement}\n${codePushHeaderImportStatement}`);
}

// 2. Modify jsCodeLocation value assignment
var oldJsCodeLocationAssignmentStatement = appDelegateContents.match(/(jsCodeLocation = .*)/)[1];
var newJsCodeLocationAssignmentStatement = "jsCodeLocation = [CodePush bundleURL];";
if (~appDelegateContents.indexOf(newJsCodeLocationAssignmentStatement)) {
    console.log(`"jsCodeLocation" already pointing to "[CodePush bundleURL]".`);
} else {
    var jsCodeLocationPatch = `
#ifdef DEBUG
    ${oldJsCodeLocationAssignmentStatement}
#else
    ${newJsCodeLocationAssignmentStatement}
#endif`;
    appDelegateContents = appDelegateContents.replace(oldJsCodeLocationAssignmentStatement,
        jsCodeLocationPatch);
}

var plistPath = getPlistPath();

if (!plistPath) {
    console.log(`Couldn't find .plist file. You might need to update it manually \
Please refer to plugin configuration section for iOS at \
https://github.com/microsoft/react-native-code-push#plugin-configuration-ios`);
    return;
}

var plistContents = fs.readFileSync(plistPath, "utf8");

// 3. Add CodePushDeploymentKey to plist file
var parsedInfoPlist = plist.parse(plistContents);
if (parsedInfoPlist.CodePushDeploymentKey) {
    console.log(`"CodePushDeploymentKey" already specified in the plist file.`);
    writePatches();
} else {
    inquirer.prompt({
        "type": "input",
        "name": "iosDeploymentKey",
        "message": "What is your CodePush deployment key for iOS (hit <ENTER> to ignore)"
    }).then(function(answer) {
        parsedInfoPlist.CodePushDeploymentKey = answer.iosDeploymentKey || "deployment-key-here";
        plistContents = plist.build(parsedInfoPlist);

        writePatches();
    });
}

function writePatches() {
    fs.writeFileSync(appDelegatePath, appDelegateContents);
    fs.writeFileSync(plistPath, plistContents);
}

// Helper that filters an array with AppDelegate.m paths for a path with the app name inside it
// Should cover nearly all cases
function findFileByAppName(array, appName) {
    if (array.length === 0 ||!appName) return null;

    for (var i = 0; i < array.length; i++) {
        var path = array[i];
        if (path && path.indexOf(appName) !== -1) {
            return path;
        }
    }

    return null;
}

function getDefaultPlistPath() {
    //this is old logic in case we are unable to find PLIST from xcode/pbxproj - at least we can fallback to default solution
    return glob.sync(`**/${package.name}/*Info.plist`, ignoreNodeModules)[0];
}

// This is enhanced version of standard implementation of xcode 'getBuildProperty' function  
// but allows us to narrow results by PRODUCT_NAME property also. 
// So we suppose that proj name should be the same as package name, otherwise fallback to default plist path searching logic
function getBuildSettingsPropertyMatchingTargetProductName(parsedXCodeProj, prop, targetProductName, build){
    var target;
    var COMMENT_KEY = /_comment$/;
    var PRODUCT_NAME_PROJECT_KEY = 'PRODUCT_NAME';

    if (!targetProductName){
        return target;
    }

    var configs = parsedXCodeProj.pbxXCBuildConfigurationSection();
    for (var configName in configs) {
        if (!COMMENT_KEY.test(configName)) {
            var config = configs[configName];
            if ( (build && config.name === build) || (build === undefined) ) {
                if (config.buildSettings[prop] !== undefined && config.buildSettings[PRODUCT_NAME_PROJECT_KEY] == targetProductName) {
                    target = config.buildSettings[prop];
                }
            }
        }
    }
    return target;
}

function getPlistPath(){
    var xcodeProjectPaths = glob.sync(`**/*.xcodeproj/project.pbxproj`, ignoreNodeModules);
    if (!xcodeProjectPaths){
        return getDefaultPlistPath();
    }

    if (xcodeProjectPaths.length !== 1) {
        console.log('Could not determine correct xcode proj path to retrieve related plist file, there are multiple xcodeproj under the solution.');
        return getDefaultPlistPath();
    }

    var xcodeProjectPath = xcodeProjectPaths[0];
    var parsedXCodeProj;

    try {
        var proj = xcode.project(xcodeProjectPath);      
        //use sync version because there are some problems with async version of xcode lib as of current version
        parsedXCodeProj = proj.parseSync();
    }
    catch(e) {
        console.log('Couldn\'t read info.plist path from xcode project - error: ' + e.message);
        return getDefaultPlistPath();
    }

    var INFO_PLIST_PROJECT_KEY = 'INFOPLIST_FILE';
    var RELEASE_BUILD_PROPERTY_NAME = "Release";
    var targetProductName = package ? package.name : null;

    //Try to get 'Release' build of ProductName matching the package name first and if it doesn't exist then try to get any other if existing
    var plistPathValue = getBuildSettingsPropertyMatchingTargetProductName(parsedXCodeProj, INFO_PLIST_PROJECT_KEY, targetProductName, RELEASE_BUILD_PROPERTY_NAME) || 
        getBuildSettingsPropertyMatchingTargetProductName(parsedXCodeProj, INFO_PLIST_PROJECT_KEY, targetProductName) ||
         parsedXCodeProj.getBuildProperty(INFO_PLIST_PROJECT_KEY, RELEASE_BUILD_PROPERTY_NAME) || 
         parsedXCodeProj.getBuildProperty(INFO_PLIST_PROJECT_KEY);

    if (!plistPathValue){
        return getDefaultPlistPath();
    }

    return path.resolve(path.dirname(xcodeProjectPath), '..', plistPathValue);    
}

var fs = require("fs");
var glob = require("glob");
var path = require("path");

var ignoreFolders = { ignore: ["node_modules/**", "**/build/**"] };
var buildGradlePath = path.join("android", "app", "build.gradle");
var manifestPath = glob.sync("**/AndroidManifest.xml", ignoreFolders)[0];

function findMainApplication() {
    if (!manifestPath) {
        return null;
    }

    var manifest = fs.readFileSync(manifestPath, "utf8");

    // Android manifest must include single 'application' element
    var matchResult = manifest.match(/application\s+android:name\s*=\s*"(.*?)"/);
    if (matchResult) {
        var appName = matchResult[1];
    } else {
        return null;
    }

    var nameParts = appName.split('.');
    var searchPath = glob.sync("**/" + nameParts[nameParts.length - 1] + ".java", ignoreFolders)[0];
    return searchPath;
}

var mainApplicationPath = findMainApplication() || glob.sync("**/MainApplication.java", ignoreFolders)[0];

// 1. Add the getJSBundleFile override
var getJSBundleFileOverride = `
    @Override
    protected String getJSBundleFile() {
      return CodePush.getJSBundleFile();
    }
`;

function isAlreadyOverridden(codeContents) {
    return /@Override\s*\n\s*protected String getJSBundleFile\(\)\s*\{[\s\S]*?\}/.test(codeContents);
}

if (mainApplicationPath) {
    var mainApplicationContents = fs.readFileSync(mainApplicationPath, "utf8");
    if (isAlreadyOverridden(mainApplicationContents)) {
        console.log(`"getJSBundleFile" is already overridden`);
    } else {
        var reactNativeHostInstantiation = "new ReactNativeHost(this) {";
        mainApplicationContents = mainApplicationContents.replace(reactNativeHostInstantiation,
            `${reactNativeHostInstantiation}\n${getJSBundleFileOverride}`);
        fs.writeFileSync(mainApplicationPath, mainApplicationContents);
    }
} else {
    var mainActivityPath = glob.sync("**/MainActivity.java", ignoreFolders)[0];
    if (mainActivityPath) {
        var mainActivityContents = fs.readFileSync(mainActivityPath, "utf8");
        if (isAlreadyOverridden(mainActivityContents)) {
            console.log(`"getJSBundleFile" is already overridden`);
        } else {
            var mainActivityClassDeclaration = "public class MainActivity extends ReactActivity {";
            mainActivityContents = mainActivityContents.replace(mainActivityClassDeclaration,
                `${mainActivityClassDeclaration}\n${getJSBundleFileOverride}`);
            fs.writeFileSync(mainActivityPath, mainActivityContents);
        }
    } else {
        console.error(`Couldn't find Android application entry point. You might need to update it manually. \
Please refer to plugin configuration section for Android at \
https://github.com/microsoft/react-native-code-push#plugin-configuration-android for more details`);
    }
}

if (!fs.existsSync(buildGradlePath)) {
    console.error(`Couldn't find build.gradle file. You might need to update it manually. \
Please refer to plugin installation section for Android at \
https://github.com/microsoft/react-native-code-push#plugin-installation-android---manual`);
    return;
}

// 2. Add the codepush.gradle build task definitions
var buildGradleContents = fs.readFileSync(buildGradlePath, "utf8");
var reactGradleLink = buildGradleContents.match(/\napply from: ["'].*?react\.gradle["']/)[0];
var codePushGradleLink = `apply from: "../../node_modules/react-native-code-push/android/codepush.gradle"`;
if (~buildGradleContents.indexOf(codePushGradleLink)) {
    console.log(`"codepush.gradle" is already linked in the build definition`);
} else {
    buildGradleContents = buildGradleContents.replace(reactGradleLink,
        `${reactGradleLink}\n${codePushGradleLink}`);
    fs.writeFileSync(buildGradlePath, buildGradleContents);
}
